import styled from "@emotion/styled";

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;

  margin-top: 10vh;
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
`;

export { Container, Img };
