  //       navigate("/");
  //     })
  //     .catch((error) => {
  //       console.error(error);
  //       alert(error);
  //     });
  // };

  return (
    <S.Container>
      <h1>Board</h1>
      <input type={"text"} placeholder={"title"} onChange={getTitle}></input>
      <input type={"text"} placeholder={"content"} onChange={getContent}></input>
      <button type={"submit"} onClick={() => addBoard(title, content)}>
        전송
      </button>
    </S.Container>
  );
}
export default Board;
