import axios from "axios";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { API_PATH, BROWSER_PATH } from "../../constants/path";

import * as S from "./index.styled";

function Board() {
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState("");

  const getTitle = (e) => {
    setTitle(e.target.value);
  };

  const getContent = (e) => {
    setContent(e.target.value);
  };

  const getImage = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();
      reader.onload = handleReaderLoaded.bind(this);
      reader.readAsBinaryString(file);
    }
  };

  const handleReaderLoaded = (e) => {
    let binaryString = e.target.result;
    setImage(btoa(binaryString));
  };

  const requestAddBoard = async (title, content) => {
    const FormData = new FormData();
    await axios
      .post(API_PATH.BOARD, {
        title: title,
        content: content,
        image: image,
      })
      .then(() => {
        navigate("/");
      })
      .catch((error) => {
        console.error(error);
        alert(error);
      });
  };

  return (
    <S.Container>
      <h1>Board</h1>
      <input type={"text"} placeholder={"title"} onChange={getTitle}></input>
      <input type={"text"} placeholder={"content"} onChange={getContent}></input>
      <input type={"file"} name={"image"} id={"file"} accept={"image/*"} onChange={getImage} />
      <button type={"submit"} onClick={() => requestAddBoard(title, content)}>
        전송
      </button>
      <button>
        <Link to={BROWSER_PATH.BASE}>HOME</Link>
      </button>
    </S.Container>
  );
}
export default Board;
